import * as React from 'react';
import autoBind from 'react-autobind';
import {Text, View,Button } from 'react-native';


export default class AlertComponent extends React.Component {
    constructor() {
        super();
        autoBind(this);
    }
     scenarioNameChangeText(value) {
        this.setState({ scenarioName: value });
    }
     scenarioDescriptionChangeText(value) {
        this.setState({ scenarioDescription: value });
    }
     handleListItemClick = (event, item) => {
        this.setState({ itemSelected: item });
    }

     render() {
        const { isOpen } = this.props;
        return (
            <View>
                {isOpen &&
                    <View style={styles.modal}>
                         <View style={[styles.modalContent]}>
                            <View style={{ display: 'flex',
                            flexDirection: 'row', width: '100%' }}>
                                <View style={{marginStart: '90px'}}/>
                                <Text  style={[styles.headerText, {alignSelf: 'center' }]}>
                                    {this.props.title}
                                </Text>
                                <View style={{ right: 0, marginLeft: '52px'}}>
                                    <Button title={''} onPress={this.props.stayButton}/>
                                </View>
                            </View>
                            <View style={{ borderBottomColor: '#e5e8ea', borderBottomWidth: 1, paddingTop: '10px' }} />
                                <View style={{ display: 'flex', paddingHorizontal: '20px', paddingVertical: '25px', flex: 50 }}>
                                    <Text style={{ textAlign: 'center', fontSize: 12, width: 'stretch', paddingBottom: '18px' }}>
                                        {this.props.description}
                                    </Text>
                                </View>
                            <View style={styles.footer}>
                                <View style={{ flexDirection: 'row', width: '100%', justifyContent: 'center'}}>
                                    <View style={{paddingHorizontal: '5px'}}>
                                        <Button  title={'Leave'} onPress={this.props.leaveButton} />
                                    </View>
                                    <View style={{paddingHorizontal: '5px'}}>
                                        <Button title={'Stay'} onPress={this.props.stayButton}/>
                                    </View>
                                </View >
                                <View style={{ flex: 3 }} />
                            </View>
                        </View>
                    </View>}
            </View>
        );
    }
}

const styles = {
    modal: {
        zIndex: 2,
        width: '100vw',
        height: '100vh',
        overflow: 'visible',
        backgroundColor: 'rgba(8, 56, 108, 0.5)',
        position: 'absolute',
        // justifyContent: 'center',
    },
    modalContent: {
        flexDirection: 'column',
        width: '85vw',
            height: '30vh',
        overflow: 'visible',
        alignSelf: 'center',
        backgroundColor: 'white',
        paddingTop: '15px',
        boxShadow: '2px',
        borderRadius: 3,
        position: 'relative',
    },
    headerText: {
        fontWeight: 'bold',
        fontSize: 14,
        fontFamily: 'Roboto,sans-serif',
        lineHeight: 16,
        textAlign: 'center',
        width: '225px',
        height: '24px',
        left: '90px',
    },
    footer:
        {
            flexDirection: 'row',
            paddingVertical: '15px',
            shadowColor: '#000',
            shadowOffset: {width: 0, height: 2},
            shadowOpacity: 0.8,
            shadowRadius: 2,
            justifyContent: 'center',
        },
};
