import React, { Component } from 'react';
import {
    Platform, StyleSheet, Text, View,
    Button, Alert,
    ActivityIndicator,
} from 'react-native';

import Geolocation from '@react-native-community/geolocation';
import autoBind from 'react-autobind';
import Geocoder from 'react-native-geocoding';  


const instructions = Platform.select({
    ios: 'Press Cmd+R to reload,\n' + 'Cmd+D or shake for dev menu',
    android:
        'Double tap R on your keyboard to reload,\n' +
        'Shake or press menu button for dev menu',
});

export default class MyGetLocation extends Component {
  constructor() {
    Geocoder.init('AIzaSyBEA7HIZFZs89iLnh1uKdXaFc5gRSVi27E');
    super();
    autoBind(this);
    this.state = {
      location: null,
      loading: false,
    };
  }
    _requestLocation = () => {
        this.setState({ loading: true, location: null });
        Geolocation.getCurrentPosition(info => {
          console.log('info------');
          console.log(info);
          console.log(info.cooders);
          console.log(info.cooders);
        });
    //     Geocoder.from(41.89, 12.49)
    //     .then(json => {
    //       var addressComponent = json.results[0].address_components[0];
    //       console.log('addressComponent------');
    //       console.log(addressComponent);
    //     })
    //     .catch(error =>
    //        console.warn(error)
    // );
    }

    render() {
        const { location, loading } = this.state;
        return (
            <View style={styles.container}>
                <Text style={styles.welcome}>Welcome to React Native!</Text>
                <Text style={styles.instructions}>To get location, press the button:</Text>
                <View style={styles.button}>
                    <Button
                        disabled={loading}
                        title="Get Location"
                        onPress={this._requestLocation}
                    />
                </View>
                {loading ? (
                    <ActivityIndicator />
                ) : null}
                {location ? (
                    <Text style={styles.location}>
                        {JSON.stringify(location, 0, 2)}
                    </Text>
                ) : null}
            </View>
        );
    }
}

const styles = StyleSheet.create({
  container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#F5FCFF',
  },
  welcome: {
      fontSize: 20,
      textAlign: 'center',
      margin: 10,
  },
  instructions: {
      textAlign: 'center',
      color: '#333333',
      marginBottom: 5,
  },
  location: {
      color: '#333333',
      marginBottom: 5,
  },
  button: {
      marginBottom: 8,
  }
});
