import React, { Component } from 'react';
import { StyleSheet, View, Image, FlatList, Dimensions } from 'react-native';
import { Text, Divider } from 'react-native-elements';
import { Collapse, CollapseHeader, CollapseBody } from 'accordion-collapse-react-native';
export default class MyForecastCard extends Component {

  _renderHeader() {
    var date = new Date(this.props.detail[0].dt * 1000);
    var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    var dayOfWeek = days[date.getDay()]
    var deg = Math.round(this.props.detail[0].main.temp * 10) / 10;
    var image={ uri: "https://openweathermap.org/img/w/" + this.props.detail[parseInt(this.props.detail.length/2)].weather[0].icon + ".png" }
    return (
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
          <Text style={styles.header}>{dayOfWeek}</Text>
          <View style={{flexDirection:'row'}}>
          <Text style={[styles.header,{paddingTop: 12}]}>{deg}&#176;</Text>
          <Image style={{ width: 50, height: 50 }} source={image} />
          </View>
        </View>
    )
  }
  _renderWeatherList(item) {
    let time;
    var date = new Date(item.item.dt * 1000);
    var hours = date.getHours();
    var minutes = "0" + date.getMinutes();
    time = hours + ':' + minutes.substr(-2);
    return (
      <View>
        <Text style={styles.time}>{time}</Text>
        <Image style={{ width: 60, height: 60 }} source={{ uri: "https://openweathermap.org/img/w/" + item.item.weather[0].icon + ".png" }} />
        <View>
          <Text style={styles.time}>{Math.round(item.item.main.temp * 10) / 10}&#8451;</Text>
        </View>
      </View>

    )
  }
  _renderContent() {
    const windowWidth = Dimensions.get('window').width;
    const { content } = this.props;
    var date = new Date(content.sunrise * 1000);
    var hours = date.getHours();
    var minutes = "0" + date.getMinutes();
    let sunrise = hours + ':' + minutes.substr(-2);

    var date = new Date(content.sunset * 1000);
    var hours = date.getHours();
    var minutes = "0" + date.getMinutes();
    let sunset = hours + ':' + minutes.substr(-2);

    return (<View>
      <Divider style={{ backgroundColor: '#dfe6e9', marginVertical: 10 }} />
      <FlatList
        style={{ width: windowWidth * 0.9 }}
        horizontal
        pagingEnabled={true}
        key={item => item.dt_text} 
        showsHorizontalScrollIndicator={true}
        data={this.props.detail}
        renderItem={this._renderWeatherList}
      />
      <Text style={styles.notes}>Sunrise: {sunrise}</Text>
      <Text style={styles.notes}>Sunset: {sunset}</Text>
      <Text style={styles.notes}>{content.description}</Text>
      <Text style={styles.notes}> {content.wind.spead} {content.wind.deg}</Text>
    </View>);
  }
  render() {
    return (
      <View style={styles.card}>
        <Collapse>
          <CollapseHeader>
            {this._renderHeader()}
          </CollapseHeader>
          <CollapseBody>
            {this._renderContent()}
          </CollapseBody>
        </Collapse>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  card: {
    padding: 15,
  },
  time: {
    fontSize: 15,
    color: 'white',
  },
  notes: {
    paddingHorizontal: 5,
    fontSize: 15,
  },
  header: {
    paddingHorizontal: 5,
    fontSize: 16,
    paddingVertical: 3,
    fontWeight: '500',
    color: 'white',
  }
});
