import React, { Component } from 'react';
import { StyleSheet, View, Text, ImageBackground, Dimensions } from 'react-native';
import MyGetLocation from '../../components/GetLocation';
import ForecastList from '../../components/ForecastList';

import * as Location from 'expo-location';
import * as Permissions from 'expo-permissions';

// import Geolocation from '@react-native-community/geolocation';
import autoBind from 'react-autobind';
// import Geocoder from 'react-native-geocoding';  


export default class App extends Component {

  constructor() {
    super();
    autoBind(this);
    this.state = {
      location: { lat: 31, lng: 34 },
      loading: true,
    };
  }
  componentDidMount() {
    this.getLocationAsync();
  }
  getLocationAsync = async () => {
    let { status } = await Permissions.askAsync(Permissions.LOCATION);
    if (status !== 'granted') {
      this.setState({
        errorMessage: 'Permission to access location was denied',
      });
    }

    let location = await Location.getCurrentPositionAsync({ accuracy: Location.Accuracy.Highest });
    const { latitude, longitude } = location.coords;
    let url = 'https://api.openweathermap.org/data/2.5/forecast?lat=' +
      latitude + '&lon=' + longitude + '&units=metric&appid=34026f1820a73bd0d6e38e6c9931d3e5';
    fetch(url)
      .then(response => response.json())
      .then(data => {
        console.log('this.props.location-------');
        console.log(data);
        var date = new Date(data.list[0].dt * 1000);
        var hours1 = date.getHours();
        this.setState({ forecast: data, location: { lat: latitude, lng: longitude }, address: data.city.name, hours: hours1, loading: false });
      });
  };
  render() {
    const { hours, address, location, forecast, loading } = this.state;
    const imageURL = { uri: `https://cdn.dribbble.com/users/925716/screenshots/3333720/attachments/${hours < 4 || hours > 20 ? '722375/night.png' : '722376/after_noon.png'}` };
    return (
      <View style={styles.container}>
        <ImageBackground style={styles.image} source={imageURL}>
          {loading&&<ActivityIndicator size="small" color="#0000ff" />}
          <View style={styles.innerContainer}>
            <Text style={styles.text}>{address}</Text>
            {forecast && <Text style={[styles.text, { fontWeight: '500', fontSize: 70 }]}>{Math.round(forecast.list[0].main.temp * 10) / 10}&#176;</Text>}
            {forecast && <ForecastList forecast={forecast} />}
          </View>
        </ImageBackground>
      </View>
    );
  }
}
const windowHighest = Dimensions.get('window').Highest;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5FCFF',
  },
  innerContainer: {
    // flexDirection: 'column-reverse',
    display: 'flex',
    paddingHorizontal: '10px',
    height: windowHighest * 0.8,
    marginTop: 200,
    justifyContent: 'center'
  },
  text: {
    color: 'white',
    alignSelf: 'center',
    fontSize: 20,
    paddingVertical: 5,

  },
  image: {
    flex: 1,
    resizeMode: "cover",
  },
});
