import React, {Component} from 'react';
import {StyleSheet, View, Text} from 'react-native';
import MyGetLocation from '../components/GetLocation';
import ForecastList from '../components/ForecastList';

import Geolocation from '@react-native-community/geolocation';
import autoBind from 'react-autobind';
import Geocoder from 'react-native-geocoding';  


export default class App extends Component {

  constructor() {
    Geocoder.init('AIzaSyBEA7HIZFZs89iLnh1uKdXaFc5gRSVi27E');
    super();
    autoBind(this);
    this.state = {
      location: null,
    };
    Geolocation.getCurrentPosition(info => {
      console.log('info------');
      console.log(info.coords.latitude);
      console.log(info.coords.longitude);
      this.setState({location:{
        lat:info.coords.latitude,
        lng:info.coords.longitude
      }})
    });
  }

  render() {
    const {location} =this.state;
    return (
      <View style={styles.container}>
        {location&&<View style={styles.banner}>
        <MyGetLocation location={location}/>
        </View>}
        {location&&<ForecastList latitude={location.lat} longitude={location.lng}/>}
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
      flex: 1,
      backgroundColor: '#F5FCFF',
  },
  banner: {
      fontSize: 10,
      textAlign: 'center',
      padding: 10,
      backgroundColor: '#80A4E4',
      borderColor:'#80A4E4',
  },
});
